// for this month bill
import React, { useState, useRef, useEffect } from "react";
import { Link } from "react-router-dom";
import { getAttendance } from '../../actions/attendance_actions'
import Loader from "../../components/Loader";
import Message from "../../components/Message"
import { useNavigate } from "react-router-dom";

import { useDispatch, useSelector } from 'react-redux'
function PreviousAttendance() {
    const dispatch = useDispatch()
    const myRef = useRef(null)
    const navigate = useNavigate()
    const [sortBy, setSortBy] = useState("Months");

    const { attendance, loading, error } = useSelector((state) => state.getAttendance);
    const { userInfo } = useSelector((state) => state.userLogin);

    useEffect(() => {
        if (userInfo && userInfo.isAdmin) {
            dispatch(getAttendance());


            console.log(attendance)

        } else {
            navigate("/login");
        }

    }, [dispatch, userInfo]);


    return (


        <div>
            <div className="h1 text-center text-dark" id="pageHeaderTitle">
                Previous Attendance From  <code>MM-YYYY</code>  to <code>MM-YYYY</code>
            </div>


            <div className="h4 text-end">
                <label for="sort">View by:</label>
                <select name="sort" value={sortBy} onChange={(e) => setSortBy(e.target.value)} ref={myRef} id="sort-select">
                    <option value="Members">Members</option>
                    <option value="Months">Months</option>
                </select>
            </div>
            {
                loading ? <Loader /> : error ? <Message variant={"danger"}>{error}</Message> :
                    (attendance.map(item => (

                        <table class="table table-striped">
                            <thead>
                                <tr>
                                    <th scope="col">#</th>
                                    <th scope="col">Year</th>
                                    <th scope="col">{sortBy}</th>
                                    <th scope="col">Go to</th>
                                </tr>
                            </thead>
                            <tbody>
                                <tr>
                                    <th scope="row" key={item.id}>{item.id}</th>
                                    <td>{item.date}</td>
                                    <td>December</td>
                                    <td>See</td>
                                </tr>
                                <tr>
                                    <th scope="row">2</th>
                                    <td>2021</td>
                                    <td>November</td>
                                    <td>See</td>
                                </tr>
                                <tr>
                                    <th scope="row">3</th>
                                    <td>2021</td>
                                    <td>October</td>
                                    <button className="button button-primary">See</button>
                                </tr>
                            </tbody>
                        </table>
                    ))

                    )
            }
        </div>
    );
}

export default PreviousAttendance;
