import React, { useState, useEffect, useReducer } from 'react'

// a button component that changes the color of the button when clicked and
//set mark the button as clicked


function Button(userId) {

    const [count, setCount] = useState(0)



    const [text, setText] = useState('Present')

    const [backgroundColor, setBackgroundColor] = useState('#056fb5')


    function handleClick() {

        setCount(count + 1)

        if (count % 2 === 0) {
            // setText('0=Absent')
            // setBackgroundColor('#FF0000')
            // setState(0)

            dispatch({ type: 'ABSENT' })
        } else if (count % 2 === 1) {
            // setText('1=Present')
            // setState(1)
            // setBackgroundColor('#0000FF')
            dispatch({ type: 'PRESENT' })

        }

    }
    const initialState = {
        status: 'Present',
    }



    function reducer(state, action) {
        switch (action.type) {
            case 'PRESENT':
                return {
                    ...state,
                    text: 'Present',
                    backgroundColor: '#056fb5',
                    status: 1
                }
            case 'ABSENT':
                return {
                    ...state,
                    text: 'Absent',
                    backgroundColor: '#ff0000',
                    status: 0
                }
            default:
                return state
        }
    }

    const [state, dispatch] = useReducer(reducer, initialState);

    // useEffect(() => {
    //     console.log(count)
    //     if (count % 3 === 0) {
    //         setText('0=Absent')
    //         setBackgroundColor('#FF0000')
    //         setState(0)
    //     } else if (count % 3 === 1) {
    //         setText('1=Present')
    //         setState(1)
    //         setBackgroundColor('#0000FF')
    //     } else if (count % 3 === 2) {
    //         setText('2=Double')
    //         setBackgroundColor('#009111')
    //         setState(2)
    //     }

    // }, [count])

    return (
        <div>
            <button className="btn" style={{ backgroundColor, "color": "white" }} onClick={() => handleClick()}>


                {text}

            </button>
        </div>
    )

}


export default Button
