import { useDispatch, useSelector } from "react-redux";

import React, { useState, useEffect, useRef } from "react";
import { getAttendance } from "../actions/attendance_actions";

export default function Counter(attendance, date) {
    const dispatch = useDispatch()
    const { attendance: getAttendanceObj, error: getAttendanceError, loading: getAttendanceLoading } = useSelector(state => state.getAttendance)


    useEffect(() => dispatch(getAttendance()))


    const countFirstTime = attendance?.filter(item => item.date === date && item.first_time === 'present').length;

    const countSecondTime = attendance?.filter(item => item.date === date && item.second_time === "present").length

    const total = countFirstTime + countSecondTime

    return total

}
