import React, { useEffect, useState } from "react";


import { getMessMenu, updateMessMenu } from "../../actions/mess_actions";

import Message from "../../components/Message";
import { useSelector, useDispatch } from "react-redux";

import "./ViewMess.css";
import Loader from "../../components/Loader";
function UpdateMess() {

    const dispatch = useDispatch()
    const [menuFirst, setMenuFirst] = useState()
    const [menuSecond, setMenuSecond] = useState()
    useEffect(() => {
        dispatch(getMessMenu());



    }, [dispatch, useSelector]);

    const { messMenu, loading, error } = useSelector((state) => state.messMenu);

    const handleChange = (e) => {
        const { name, value } = e.target;
        if (name === "firstTime") {
            setMenuFirst(value)
        } else if (name === "secondTime") {
            setMenuSecond(value)
        }

        console.log({ name, value })
    }

    const saveHandler = () => {
        console.log({ menuFirst, menuSecond })
    }

    return (
        <section id="tabs" class="project-tab">
            <div class="container">
                <div class="row">
                    <div class="col-md-12">

                        <button class="btn btn-primary" onClick={saveHandler}>Save</button>



                    </div>

                    <div class="col-md-12">
                        <div class="tab-content" id="nav-tabContent">
                            <div
                                class="tab-pane fade show active"
                                id="nav-home"
                                role="tabpanel"
                                aria-labelledby="nav-home-tab"
                            >
                                {loading ? <Loader /> : error ? <Message variant='danger'>{error}</Message>
                                    : (
                                        <form >

                                            <table class="table" cellspacing="0">
                                                <thead>
                                                    <tr>
                                                        <th>Day</th>
                                                        <th>First Time</th>
                                                        <th>Second Time</th>
                                                    </tr>
                                                </thead>

                                                <tbody>
                                                    {messMenu?.map((menu) => (

                                                        <tr key={menu.id}>
                                                            <td >
                                                                <a href="#">{menu.day}</a>
                                                            </td>
                                                            <td className="form-group">
                                                                <input type="text" name="firstTime" className="form-control" value={menu.first_time} onChange={(e) => handleChange(e)} />

                                                            </td>
                                                            <td className="form-group">
                                                                <input type="text" name="secondTime" className="form-control" value={menu.second_time} onChange={(e) => handleChange(e)} />

                                                            </td>
                                                        </tr>
                                                    ))}


                                                </tbody>
                                            </table>
                                        </form>
                                    )}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    );
}

export default UpdateMess;
