import Button from "../../components/Button";
import { listUsers, deleteUser } from "../../actions/user_actions";
import React, { useState, useEffect } from "react";

import { useDispatch, useSelector } from "react-redux";
import Loader from "../../components/Loader";
import Message from "../../components/Message";
import { Link, useLocation, useNavigate } from "react-router-dom";
import { markAttendance } from "../../actions/user_actions";




function MarkAttendance() {

  const dispatch = useDispatch();
  const navigate = useNavigate();







  const { users, loading, error } = useSelector((state) => state.userList);
  const today = new Date().toISOString().substr(0, 10);
  const { userInfo } = useSelector((state) => state.userLogin);


  // fn to mark attendance of all the students
  // const mark = () => {
  //   dispatch(markAttendance(
  //     {

  //       // userId: userInfo.id,
  //       userId: 1,
  //       date: today,
  //       time: new Date().toLocaleTimeString(),
  //       status: "Present"


  //     }



  //   ));

  // }

  useEffect(() => {
    if (userInfo && userInfo.isAdmin) {
      dispatch(listUsers());



    } else {
      navigate("/login");
    }

  }, [dispatch, userInfo, navigate]);



  return (
    <>
      <section>
        {/* table for all the users */}
        {/* heading for attendance */}
        <div className="row">
          <div className="col-md-12 text-dark">
            <h3>Mark Attendance</h3> for <h4>{today}</h4>'s meal
          </div>
        </div>
        {loading ? (
          <Loader />
        ) : error ? (
          <Message variant="danger">{error}</Message>
        ) : (

          <table className="table table-bordered">
            <thead>
              <tr>
                <th scope="col">#</th>
                <th scope="col">Name</th>
                <th scope="col">Room no.</th>
                <th scope="col">First Time</th>
                <th scope="col">Second Time</th>
              </tr>
            </thead>
            <tbody>
              <form onSubmit={handleSubmit}>
                {users.map((user) => (



                  <tr key={user.id}>


                    <th scope="row">1</th>
                    <td>
                      <input
                        type="text"
                        id="userName"
                        value={user.username}

                      />
                    </td>
                    <td>
                      <input
                        type="text"
                        id="room"
                        value={user.room} />
                    </td>

                    <td>
                      <input
                        type="text"
                        id="firstTime"
                      >

                        <Button userId={user.id} />

                      </input>


                    </td>
                    <td>
                      <input
                        type="text"
                        id="secondTime"
                      >
                        <Button userId={user.id} />
                      </input>

                    </td>
                  </tr>
                ))}

                <button className="btn btn-primary"
                  type="submit">Submit</button>
              </form>
            </tbody>


          </table>

        )}





      </section >
      <section>
        {/* bootstrap table which shows total attenandance , absentees doubles and total members  */}
        <div className="row">
          <div className="col-md-12 text-dark">
            <h3>{today}'s totals </h3>
          </div>
        </div>
        <table className="table table-bordered">
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col">Total Members</th>
              <th scope="col">Total Absentees</th>
              <th scope="col">Total Attendences</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <th scope="row">1</th>
              <td>{users?.length}</td>
              <td></td>
              <td>2</td>

            </tr>
          </tbody>
        </table>


      </section>
    </>
  );
}



export default MarkAttendance;
