// for this month bill
import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { counter } from "../../../components/counter"

import { useDispatch, useSelector } from "react-redux";
import { getBill } from '../../../actions/expenses_actions'
import Message from "../../../components/Message";
import Loader from "../../../components/Loader";
import Downloader from '../../../components/Downloader';


function ThisMonthBill() {
  const dispatch = useDispatch();
  const today = new Date().toISOString().substring(0, 7);
  useEffect(() => {
    dispatch(getBill(date.substring(0, 7)))
  }, [date, bill])

  const [bill, loading] = useSelector(state => state.getBill)
  const [date, setDate] = useState(today)



  return (
    <div>
      <div class="h1 text-center text-dark" id="mess-bill">
        Mess Bill



        <form className="form form-control" onSubmit={handleSubmit} >
          <input type="month" id="date" value={date} onChange={(e) => setDate(e.target.value)} max={today} />

          <button className="button " type="submit">Get</button>
        </form>
      </div>


      <Downloader htmlInputId={`mess-bill`} name={"Expenses-sheet"} />

      {/* boostrap table for all the users , room no , current bill added */}
      <table class="table table-striped">
        <thead>
          <tr>
            <th scope="col">#</th>
            <th scope="col">Name</th>
            <th scope="col">Room No</th>
            <th scope="col">Current Bill</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <th scope="row">1</th>
            <td>Hasnain Sikandar</td>
            <td>43</td>
            <td>+100</td>
          </tr>
          <tr>
            <th scope="row">2</th>
            <td>Muzaffar Jamil</td>
            <td>54</td>
            <td>900+</td>
          </tr>
          <tr>
            <th scope="row">3</th>
            <td>Myan Bazaan</td>
            <td>11</td>
            <td>+400</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}

export default ThisMonthBill;
