// for this month bill
import React, { useState, useEffect, useRef } from "react";
import { useSelector, useDispatch } from "react-redux";
import { getAttendance } from "../../actions/attendance_actions"
import { useNavigate } from "react-router-dom"


function PreviousAttendance() {
    const dispatch = useDispatch();
    const navigate = useNavigate();


    const { userInfo } = useSelector((state) => state.userLogin);

    const { attendance, error, loading } = useSelector(state => state.getAttendance)

    useEffect(() => {
        if (userInfo && userInfo.isAdmin) {
            dispatch(getAttendance)

        } else {
            navigate('./login')
        }
    }, [navigate, dispatch, userInfo])


    return (
        <div>
            <div class="h1 text-center text-dark" id="pageHeaderTitle">
                Previous Attendance  of  <code>MM-YYYY</code>
            </div>



            {/* boostrap table for all the users , room no , current bill added */}
            <table class="table table-striped">
                <thead>
                    <tr>
                        <th scope="col">#</th>
                        <th scope="col">Studants</th>
                        <th scope="col">First Time</th>
                        <th scope="col">Second Time</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <th scope="row">1</th>
                        <td>2021</td>
                        <td>December</td>
                        <td>See</td>
                    </tr>
                    <tr>
                        <th scope="row">2</th>
                        <td>2021</td>
                        <td>November</td>
                        <td>See</td>
                    </tr>
                    <tr>
                        <th scope="row">3</th>
                        <td>2021</td>
                        <td>October</td>
                        <button className="button button-primary">See</button>
                    </tr>
                </tbody>
            </table>
        </div>
    );
}

export default PreviousAttendance;
