import React, { useEffect } from "react";
import { saveAs } from "file-saver";

import { getMessMenu } from "../../actions/mess_actions";

import Message from "../../components/Message";
import { useSelector, useDispatch } from "react-redux";

import "./ViewMess.css";
import Loader from "../../components/Loader";
function ViewMess() {

  const dispatch = useDispatch()

  useEffect(() => {
    dispatch(getMessMenu());



  }, [dispatch, useSelector]);

  const { messMenu, loading, error } = useSelector((state) => state.messMenu);

  const saveFile = () => {
    saveAs(
      "https://www.w3.org/WAI/ER/tests/xhtml/testfiles/resources/pdf/dummy.pdf",
      "Mess Menu.pdf"
    );
  };

  return (
    <section id="tabs" class="project-tab">
      <div class="container">
        <div class="row">
          <div class="col-md-12">

            <button class="btn btn-primary" onClick={saveFile}> Download PDF</button>

            <button class="btn btn-primary">Edit</button>



          </div>

          <div class="col-md-12">
            <div class="tab-content" id="nav-tabContent">
              <div
                class="tab-pane fade show active"
                id="nav-home"
                role="tabpanel"
                aria-labelledby="nav-home-tab"
              >
                {loading ? <Loader /> : error ? <Message variant='danger'>{error}</Message>
                  : (
                    <table class="table" cellspacing="0">
                      <thead>
                        <tr>
                          <th>Day</th>
                          <th>First Time</th>
                          <th>Second Time</th>
                        </tr>
                      </thead>

                      <tbody>
                        {messMenu.map((menu) => (

                          <tr key={menu.id}>
                            <td>
                              <a href="#">{menu.day}</a>
                            </td>
                            <td>{menu.firstTime}</td>
                            <td>{menu.secondTime}</td>
                          </tr>
                        ))}


                      </tbody>
                    </table>
                  )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}

export default ViewMess;
